var gulp = require('gulp');
var del = require('del');
var vinylPaths = require('vinyl-paths');
var paths = require('../paths');

// Clean minified assets generated by the connection-asset plugin
// Assets are generated in a production environment
// NODE_ENV == 'production'
gulp.task('clean:assets', function() {
  return gulp.src('builtAssets/')
    .pipe(vinylPaths(del));
});

// Clean coverage report generated by Istanbal
gulp.task('clean:coverage', function() {
  return gulp.src('test/coverage')
    .pipe(vinylPaths(del));
});

// Clean the selenium standalone driver and the chrome web driver
gulp.task('clean:lib', function() {
  return gulp.src(paths.e2eLib)
    .pipe(vinylPaths(del));
});
