var gulp = require('gulp');
var del = require('del');
var vp = require('vinyl-paths');
var paths = require('../paths');

/**
 * Deletes /builtAssets
 * These files are generated by connect-assets when NODE_ENV=production
 */
gulp.task('clean:assets', function () {
  return gulp.src(paths.assets)
    .pipe(vp(del));
});

/** Deletes /bin/coverage */
gulp.task('clean:coverage', function () {
  return gulp.src(paths.coverage)
    .pipe(vp(del));
});

/** Deletes /bin/drivers */
gulp.task('clean:drivers', function () {
  return gulp.src(paths.drivers)
    .pipe(vp(del));
});

/** Deletes /bin/reports */
gulp.task('clean:reports', function () {
  return gulp.src(paths.reports)
    .pipe(vp(del));
});

/** Deletes /bin */
gulp.task('clean:bin', [
  'clean:coverage',
  'clean:drivers',
  'clean:reports'
]);
